int n;

int get_max_sub(vector<vector<int> > & grid) {
    // make cumulative grid
    int cum[2 * n][2 * n];
    for (int i = 0; i < 2 *n; ++i) {
        for (int j = 0; j < 2 * n; ++j) {
            cum[i][j] = grid[i][j];
            if (i > 0) cum[i][j] += cum[i - 1][j];
            if (j > 0) cum[i][j] += cum[i][j - 1];
            if (i > 0 && j > 0) cum[i][j] -= cum[i - 1][j - 1];
        }
    }

    int result = 75 * -100;
    // starting coordinate
    for (int a = 0; a < 2 * n; ++a) {
        for (int b = 0; b < 2 * n; ++b) {

            // ending coordinate
            for (int x = a; x < 2 * n && x < (a + n); ++x) {
                for (int y = b; y < 2 * n && y < (b + n); ++y) {
                    int temp = cum[x][y];
                    if (a > 0) temp -= cum[a - 1][y];
                    if (b > 0) temp -= cum[x][b - 1];
                    if (a > 0 && b > 0) temp += cum[a - 1][b - 1];
                    result = max(result, temp);
                }
            }

        }
    }
    return result;
}

int main () {

    // read input
    int q; scanf("%d", &q);
    while (q --> 0) {
        scanf("%d", &n);
        vector<vector<int> > grid(2*n, vector<int>(2*n));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                scanf("%d", &grid[i][j]);
                grid[i][j + n] = grid[i][j];
                grid[i + n][j] = grid[i][j];
                grid[i + n][j + n] = grid[i][j];

            }
        }

        cout << get_max_sub(grid) << endl;
    }
    return 0;
}

class Solution {
public:
        int coinChange(vector<int>& coins, int amount) {
        int Max = amount + 1;
        int coinlen = coins.size();
        vector<int> dp(amount + 1, Max);
        dp[0] = 0;
        for (int i = 0; i < amount + 1; ++i)
        {
            for (int j = 0; j < coinlen; ++j)
            {
                if(coins[j] <= i)
                    dp[i] = min(dp[i], dp[i - coins[j]] + 1);
            }
        }
        return dp[amount] > amount ? -1 : dp[amount];

    }
};

# Definition for a binary tree node.
# class TreeNode(object):
#     def __init__(self, x):
#         self.val = x
#         self.left = None
#         self.right = None

class Solution(object):
    def flatten(self, root):
        """
        :type root: TreeNode
        :rtype: void Do not return anything, modify root in-place instead.
        """
        stack = []
        if root:
            if root.right:
                stack.append(root.right)
            if root.left:
                stack.append(root.left)
            curr = root

            while stack:
                temp = stack.pop()
                curr.right = temp
                curr.left = None

                if temp:
                    if temp.right:
                        stack.append(temp.right)
                    if temp.left:
                        stack.append(temp.left)

                curr = temp

def convert_binary(n):
    a = str(bin(n))
    return a[2:]




def compute_tree(decisions):
    p = 1
    q = 1
    for i in decisions[1:]:
        if i == '1':
            q = p + q
        else:
            p = p+q


    return (p,q)


