class Solution {
 public:
  int count(int n, vector<int>& dp) {
    if (dp[n] != -1) return dp[n];
    dp[n] = 0;
    for (int i = 1; i <= n; ++i) dp[n] += count(i - 1, dp) * count(n - i, dp);
    return dp[n];
  }

  int numTrees(int n) {
    vector<int> dp(n + 1, -1);
    dp[0] = dp[1] = 1;
    return count(n, dp);
  }
};

