  bool first = true;
  for (int ca = 1; GET(c) && GET(s) && GET(q) && (c || s || q); ++ca) {
    vector<edge> edges;
    for (int i = 0; i < s; ++i) {
      edge e;
      GET(e.c1);
      GET(e.c2);
      GET(e.d);
      edges.push_back(e);
    }
    sort(edges.begin(), edges.end());

    if (first)
      first = false;
