package main

import "fmt"

type vec [2]int
type line [2]vec

// Represent lines from a to b with base point c and vector m
// Reduce m, s.t. m[0] >= 0
// and m[0] and m[1] are coprime e.g. (12, 8) -> (3, 2)
func makeLine(a, b vec) line {
	dx := a[0] - b[0]
	dy := a[1] - b[1]
	if dx < 0 {
		dx *= -1
		dy *= -1
	}
	for d := 2; d <= dx && d <= dy; d++ {
		for dx%d == 0 && dy%d == 0 {
			dx /= d
			dy /= d
		}
	}
	if dx > 1 && dy%dx == 0 {
		dy /= dx
		dx = 1
	}
	if (dy > 1 || dy < -1) && dx%dy == 0 {
		dx /= dy
		dy = 1
	} else if dx == 0 {
		dy = 1
	}
	m := vec{dx, dy}
	c := a
	for m[0] > 0 && c[0] > 0 {
		c[0] -= m[0]
		c[1] -= m[1]
	}
	for m[0] > 0 && c[0] < 0 {
		c[0] += m[0]
		c[1] += m[1]
	}
	if m[0] == 0 {
		c[1] = 0
	}
	return line{m, c}
}

func maxPoints(points [][]int) int {
	if len(points) == 0 {
		return 0
	}
	// Count occurances of the same point
	// Remeber which indicies are first occurances
	// Find num of occurances of most common point
	pMax := 1
	pCounts := make(map[vec]int)
	pStarts := make(map[vec]int)
	for i, p := range points {
		a := vec{p[0], p[1]}
		if pCounts[a] == 0 {
			pStarts[a] = i
		}
		pCounts[a]++
		if pCounts[a] > pMax {
			pMax = pCounts[a]
		}
	}

	// Make lines from each point to each other point
	// Count duplicate lines
	// Skip duplicate points
	lines := make(map[line]int)
	lineStarters := make(map[line]int)
	for i, p := range points {
		a := vec{p[0], p[1]}
		if pStarts[a] != i {
			continue
		}

		for j, q := range points[i+1:] {
			b := vec{q[0], q[1]}
			if pStarts[b] != i+1+j {
				continue
			}

			l := makeLine(a, b)
			if lines[l] == 0 {
				lines[l] += pCounts[a]
				lines[l] += pCounts[b]
				lineStarters[l] = i
			} else if lineStarters[l] == i {
				lines[l] += pCounts[b]
			}
		}
	}

	// Count line with most occurances
	// Equal points always lie on the same line
	c := pMax
	for _, x := range lines {
		if x > c {
			c = x
		}
	}
	return c
}

