from collections import defaultdict


def kosaraju(g, g_rev):
    order = []
    visited = set()

    def visit(u):
        visited.add(u)
        for v in g[u]:
            if v not in visited:
                visit(v)
        order.append(u)

    for u in g:
        if u not in visited: visit(u)

    components = []
    visited.clear()

    def build_comp(u):
        components[-1].append(u)
        visited.add(u)
        for v in g_rev[u]:
            if v not in visited:
                build_comp(v)

    for u in order[::-1]:
        if u not in visited:
            components.append([])
            build_comp(u)

    return components


