class Solution:
    def solve(self, board: List[List[str]]) -> None:
        """
        Using union-find
        """
        if len(board) == 0: # edge case
            return
        factions = [[(i,j) for j in range(len(board[i]))] for i in range(len(board))]
        def get_root(r,c):
            rr,rc = factions[r][c]
            while (rr,rc) != factions[rr][rc]:
                rr,rc = factions[rr][rc]
            factions[r][c] = (rr,rc)
            return (rr,rc)
        # walk along edges doing union-find for each faction
        for i in range(len(board)-1):
            for j in range(len(board[i])-1):
                crr,crc = get_root(i,j)
                if board[i][j] == board[i+1][j]:
                    lrr,lrc = get_root(i+1,j)
                    factions[lrr][lrc] = (crr,crc)
                if board[i][j] == board[i][j+1]:
                    rrr,rrc = get_root(i,j+1)
                    factions[rrr][rrc] = (crr,crc)
        edgy_factions = set()
        edgy_factions.update({get_root(i,0) for i in range(len(board))})
        edgy_factions.update({get_root(i,-1) for i in range(len(board))})
        edgy_factions.update({get_root(0,i) for i in range(len(board[0]))})
        edgy_factions.update({get_root(-1,i) for i in range(len(board[0]))})
        for i in range(len(board)):
            for j in range(len(board[i])):
                if get_root(i,j) not in edgy_factions:
                    board[i][j] = 'X'

