class Solution {
    // Dynamic Programming
    // Store solutions:
    var maxProd = mutableListOf(0, 1, 1)

    fun integerBreak(n: Int): Int {
        // Look up solution if possible
        if (maxProd.size > n) {
            return maxProd[n]
        }

        // Try all n = i + j
        var m = 1
        for (i in 1..n/2) {
            var j = n - i
            var a = integerBreak(i)
            var b = integerBreak(j)
            if (a < i) a = i
            if (b < j) b = j
            var p = a * b
            if (p > m) m = p
        }
        maxProd.add(m)
        return m
    }
}

