string organizingContainers(vector<vector<int>> container) {
    vector<int> container_caps;
    vector<int> ball_totals;
    for (int i = 0; i < container.size(); ++i) {
        long long row_total = 0;
        for (int j = 0; j < container[i].size(); ++j) {
            row_total += container[i][j];
        }

        long long col_total = 0;
        for (int j = 0; j < container[i].size(); ++j) {
            col_total += container[j][i];
        }

        container_caps.push_back(row_total);
        ball_totals.push_back(col_total);
    }

    sort(container_caps.begin(), container_caps.end());
    sort(ball_totals.begin(), ball_totals.end());

    if (container_caps == ball_totals)
