    def longestPalindrome(self, s: str) -> str:
        l = len(s)
        M = [[0]*l for x in range(l)]
        maximum = 0
        ans = ''
        for i in range(l-1,-1,-1):
            for j in range(l-1,-1,-1):
                if (i==j):
                    M[i][j] = 1
                    if (M[i][j] and (j-i+1>maximum)):
                        ans = s[i:j+1]
                        maximum = j-i+1
                if (i+1==j):
                    M[i][j] = s[i]==s[j]
                    if (M[i][j] and (j-i+1>maximum)):
                        ans = s[i:j+1]
                        maximum = j-i+1

                elif (j>i):
                    M[i][j] = (M[i+1][j-1]) & (s[i] == s[j])
                    if (M[i][j] and (j-i+1>maximum)):
                        ans = s[i:j+1]
                        maximum = j-i+1

