int unboundedKnapsack(int k, vector<int> arr) {
    bool sum[k+1];
    sum[0]=true;
    for(int i=1; i<=k; i++){
        sum[i]=false;
        for(int j=0; j<arr.size(); j++){
            int k=i-arr[j];
            if(k>=0){
                if(sum[k]){
                    sum[i]=true;
                    break;
                }
            }
        }
    }
    while(!sum[k]){
        k--;
    }return k;
}

