          median_ = median_->next;
        } else {
          // do nothing
        }
      } else {
        if (num >= median_->val) {
          // do nothing
        } else {
          median_ = median_->prev;
        }
      }
    }
  }

  double findMedian() {
    /*printf("BEGIN\n");
