# Complete the evenForest function below.
def evenForest(t_nodes, t_edges, t_from, t_to):
    tree = defaultdict(set)
    for i in range(t_edges):
        tree[t_to[i]-1].add(t_from[i]-1)
    num_nodes = [-1]*t_nodes
    cuts = set()
    def recurse_num_nodes(n):
        if num_nodes[n] > -1:
            return num_nodes[n]
        elif len(tree[n]) == 0:
            num_nodes[n] = 1
            return recurse_num_nodes(n)
        else:
            num_nodes[n] = 1+sum(recurse_num_nodes(node) for node in tree[n])
            if num_nodes[n]%2==0:
                cuts.add(n)
                num_nodes[n] = 0
            return recurse_num_nodes(n)
    recurse_num_nodes(0)
    return len(cuts)-1
    

