import java.io.*;
import java.util.*;
import java.text.*;

public class Solution {
    static TreeMap<Long, Integer> left;
    static TreeMap<Long, Integer> right;
    static boolean even;
    static double median;

    static void addLeft(long x) {
        if (left.containsKey(x) == false) {
            left.put(x, 1);
        }else{
            left.put(x, left.get(x) + 1);
        }
    }

    static void removeLeft(long x) {
        if (left.get(x) == 1) {
            left.remove(x);
        }else{
            left.put(x, left.get(x) - 1);
        }
    }

    static void addRight(long x) {
        if (right.containsKey(x) == false) {
            right.put(x, 1);
        }else{
            right.put(x, right.get(x) + 1);
        }
    }

    static void removeRight(long x) {
        if (right.get(x) == 1) {
            right.remove(x);
        }else{
            right.put(x, right.get(x) - 1);
        }
    }

    static void add(long x) {
        if (even) {
            if (x < median) {
                addLeft(x);

                long lMax = left.lastKey();
                removeLeft(lMax);
                even = false;
                median = lMax;
            }else{
                addRight(x);

                long rMin = right.firstKey();
                removeRight(rMin);
                even = false;
                median = rMin;
            }
        }else{
            if (x < median) {
                addLeft(x);
                addRight((long) median);
            }else{
                addRight(x);
                addLeft((long) median);
            }

            long lMax = left.lastKey();
            long rMin = right.firstKey();
            even = true;
            median = (lMax + rMin) / 2.0;
        }
    }

    static boolean remove(long x) {
        if (even) {
            if (left.size() == 0 || right.size() == 0) {
                return false;
            }
            if (x < median) {
                if (left.containsKey(x) == false) {
                    return false;
                }
                removeLeft(x);
                long rMin = right.firstKey();
                removeRight(rMin);
                even = false;
                median = rMin;
            }else{
                if (right.containsKey(x) == false) {
                    return false;
                }
                removeRight(x);
                long lMax = left.lastKey();
                removeLeft(lMax);
                even = false;
                median = lMax;
            }
        }else{
            if (x == median) {
                if (left.size() == 0 || right.size() == 0) {
                    even = true;
                    median = 0.0;
                }else{
                    long lMax = left.lastKey();
                    long rMin = right.firstKey();
                    even = true;
                    median = (lMax + rMin) / 2.0;
                }
                return true;
            }else if (left.size() == 0 || right.size() == 0) {
                return false;
            }

            if (x < median) {
                if (left.containsKey(x) == false) {
                    return false;
                }
                removeLeft(x);
                addLeft((long) median);
                long lMax = left.lastKey();
                long rMin = right.firstKey();
                even = true;
                median = (lMax + rMin) / 2.0;
            }else{
                if (right.containsKey(x) == false) {
                    return false;
                }
                removeRight(x);
                addRight((long) median);
                long lMax = left.lastKey();
                long rMin = right.firstKey();
                even = true;
                median = (lMax + rMin) / 2.0;
            }
        }
        return true;
    }

    public static void main(String[] args) {
        left = new TreeMap<>();
        right = new TreeMap<>();
        even = true;
        median = 0.0;

        Scanner in = new Scanner(System.in);
        int n = Integer.parseInt(in.nextLine());

        for (int i = 0; i < n; i++) {
            String line = in.nextLine();
            int x = Integer.parseInt(line.substring(2));
            boolean wrong = false;
            if (line.startsWith("a")) {
                add(x);
            }else{
                wrong = !remove(x);
            }
            if (wrong) {
                System.out.println("Wrong!");
            }else if (left.size() == 0 || right.size() == 0) {
                if (even) {
                    System.out.println("Wrong!");
                }else{
                    System.out.println((int)median);
                }
            }else{
                if (even) {
                    if (median == (int)median) {
                        System.out.println((int)median);
                    }else{
                        DecimalFormat df = new DecimalFormat("#.#");
                        System.out.printf(df.format(median));
                    }
                }else{
                    System.out.println((int)median);
                }
            }
        }
    }
}

