package main

import (
	"bufio"
	"fmt"
	"io"
	"os"
	"sort"
	"strconv"
	"strings"
)

// Complete the solve function below.
func solve(a []int) (sol []int) {
	min := 100000000
	sum := 0
	for _, x := range a {
		sum += x
		if x < min {
			min = x
		}
	}

	factors := make(map[int]int)
	t := sum
	for d := 2; d <= t; d++ {
		for t%d == 0 {
			factors[d]++
			t /= d
		}
	}

	posSizes := make(map[int]bool)
	posSizes[1] = true
	divs := []int{1}
	for f, c := range factors {
		e := len(divs)
		g := 1
		for a := 1; a <= c; a++ {
			g *= f
			for i := 0; i < e; i++ {
				n := divs[i] * g
				divs = append(divs, n)
				if n >= min {
					posSizes[n] = true
				}
			}
		}
	}

outer:
	for s := range posSizes {
		b := 0
		for _, x := range a {
			b += x
			if b > s {
				posSizes[s] = false
				continue outer
			} else if b == s {
				b = 0
			}
		}
	}

	for s, ok := range posSizes {
		if !ok {
			continue
		}

		sol = append(sol, s)
	}
	sort.Ints(sol)
	return
}

func main() {
	reader := bufio.NewReaderSize(os.Stdin, 1024*1024)

	stdout, err := os.Create(os.Getenv("OUTPUT_PATH"))
	checkError(err)

	defer stdout.Close()

	writer := bufio.NewWriterSize(stdout, 1024*1024)

	aCount, err := strconv.ParseInt(readLine(reader), 10, 64)
	checkError(err)

	aTemp := strings.Split(readLine(reader), " ")

	var a []int

	for aItr := 0; aItr < int(aCount); aItr++ {
		aItemTemp, err := strconv.ParseInt(aTemp[aItr], 10, 64)
		checkError(err)
		aItem := int(aItemTemp)
		a = append(a, aItem)
	}

	result := solve(a)

	for resultItr, resultItem := range result {
		fmt.Fprintf(writer, "%d", resultItem)

		if resultItr != len(result)-1 {
			fmt.Fprintf(writer, " ")
		}
	}

	fmt.Fprintf(writer, "\n")

	writer.Flush()
}

func readLine(reader *bufio.Reader) string {
	str, _, err := reader.ReadLine()
	if err == io.EOF {
		return ""
	}

	return strings.TrimRight(string(str), "\r\n")
}

func checkError(err error) {
	if err != nil {
		panic(err)
	}
}

