#include <iostream>
using namespace std;

void swap(char * attack, int index) {
  char temp = attack[index];
  attack[index] = attack[index + 1];
  attack[index + 1] = temp;
}

int get_total_attack(char * attack) {
  // Get initial total attack
  int attack_value = 1;
  int total_attack = 0;
  for (int j = 0; attack[j] != '\0'; ++j) {
    if (attack[j] == 'C') attack_value *= 2;
    else if (attack[j] == 'S') total_attack += attack_value;
  }
  return total_attack; 
}

int main() {
  int t; scanf("%d", &t);
  for (int i = 1; i <= t; ++i) {

    // Read the input 
    int D;
    char attack[256]; 
    scanf("%d %s", &D, attack);

    // Scan for impossibility
    int s_count = 0;
    for (int j = 0; attack[j] != '\0'; ++j)
      if (attack[j] == 'S') s_count += 1;
    if (s_count > D) {
      cout << "Case #" << i << ": IMPOSSIBLE" << endl;
      continue;
    }

    // Get initial total attack
    int total_attack = get_total_attack(attack);

    int hacks = 0;
    while (total_attack > D) {
      int swap_index = -1;
      for (int j = 0; attack[j + 1] != '\0'; ++j) {
        if (attack[j] == 'C' && attack[j + 1] == 'S')
          swap_index = j;
      }
      // Do the swap
      swap(attack, swap_index);
      hacks += 1;
      total_attack = get_total_attack(attack);
    }

    cout << "Case #" << i << ": " << hacks << endl;
  }
  return 0;
}

