    vector<int> number(length(s_number), 0);
    for (int j = 0; j < number.size(); ++j) {
      number[j] = s_number[j] - '0';
    }

    // Do backward pass
    for (int j = number.size() - 1; j - 1 >= 0; --j) {
      if (number[j - 1] > number[j]) {
        number[j - 1] -= 1;
        number[j] = 9;
      }
    }

    // Do forward pass
    for (int j = 0; j < number.size(); ++j) {
      if (number[j] > number[j + 1]) {
        number[j + 1] = 9;
      }
    }

    // Find the stop index
    int stop_index;
    for (int j = 0; j < number.size(); ++j) {
      if (number[j] != 0) {
        stop_index = j - 1;
        break;
      }
    }

