class Solution {
    func findRotateSteps(_ ring: String, _ key: String) -> Int {
        struct State {
            var steps:Int
            var current:Int
        }
        var states = [Int: State]()
        states[0] = State(steps: 0, current:0)
        for c in key {
            var oldStates = states
            var newStates = [Int: State]()
            for i in 0..<ring.count {
                if c != ring[ring.index(ring.startIndex, offsetBy:i)] {
                    continue
                }
                for (_, s) in oldStates {
                    var m = min(abs(s.current - i), 
                                abs(ring.count + s.current - i),
                                abs(ring.count + i - s.current))
                    if let t = newStates[i] {

                        if t.steps <= s.steps+m+1 {
                            continue
                        }
                        newStates[i] = State(steps: s.steps+m+1, current: i)
                    }else{
                        newStates[i] = State(steps: s.steps+m+1, current: i)
                    }
                }
            }
            states = newStates
        }
        var m = 10000000
        for (_, s) in states {
            m = min(m, s.steps)
        }
        return m
    }
}

