int brokenCalc(int X, int Y) {
    int steps = 0;
    while (X != Y) {
        if (X > Y) {
            steps += X - Y;
            X = Y;
        }else if (Y >= 2 * X && Y % 2 == 0) {
            Y /= 2;
            steps++;
        }else if (Y >= 2 * X && Y % 2 == 1) {
            Y++;
            steps++;
        }else if (Y >= 2 * X) {
            X *= 2;
            steps++;
        }else if (2 * X - Y <= Y - 2 * (X - 1)) {
            X *= 2;
            steps++;
        }else{
            X--;
            steps++;
        }
    }
    return steps;
}

