func largestSumAfterKNegations(A []int, K int) int {
    sort.Ints(A)
    var i int
    for i = 0; i < K; i++ {
        if A[i] < 0 {
            A[i] *= -1
        }else if A[i] == 0 {
            break
        }else{
            if (K - i) % 2 == 0 {
                break
            }
            if i == 0 {
                A[i] *= -1
            }else if A[i - 1] > A[i] {
                A[i] *= -1
            }else{
                A[i - 1] *= -1
            }
            break
        }
    }

    var sum int
    for _, v := range A {
        sum += v
    }
    return sum
}

