def binary_search(key, powerList, low, high):
    if low > high:
        return low - 1
    mid = int((low + high)/2)
    if (key < powerList[mid]):
        return binary_search(key, powerList, low, mid - 1)
    elif (key > powerList[mid]):
        return binary_search(key, powerList, mid + 1, high)
    else:
        return mid

# Complete the counterGame function below.
def counterGame(n):
    winnerMap = {
        'Louise':'Richard',
        'Richard':'Louise'
    }
    # compute all powers of 2
    powerSet = set()
    powerList = []
    for i in range(65):
        powerSet.add(2**i)
        powerList.append(2**i)
    winner = 'Richard'
    while n != 1:
        if n in powerSet:
            n = n/2
            winner = winnerMap[winner]
        else:
            index = binary_search(n, powerList, 0, len(powerList)-1)
            n = n - powerList[index]
            winner = winnerMap[winner]

    return winner


if __name__ == '__main__':
    fptr = open(os.environ['OUTPUT_PATH'], 'w')

    t = int(input())

