long long gcd(long long a,long long b)
{
    return b==0 ? a : gcd(b,a%b);
}

long long lcm(long long a,long long b)
{
    return a*b/gcd(a,b);
}

void solve(int a,int operat)
{
    int temp = a;
    int cnt = 0;
    for(cnt=0;cnt<9;cnt++)
    {
        if(temp%2==0)
        {
            if(used[(1<<cnt)+a]==0)
            {
                used[(1<<cnt)+a] = 1;
                times[(1<<cnt)+a] = lcm(cnt+2,times[a]);
                result += operat*(n/times[(1<<cnt)+a]);
                solve((1<<cnt)+a,-operat);
            }
        }
        temp /= 2;
    }
}

