class Solution {
    int[] factions;
    
    public int root_get(int n){
        int r = factions[n];
        while(factions[r] != r){
            r = factions[r];
        }
        factions[n] = r;
        return r;
    }
    
    public int findCircleNum(int[][] M) {
        int N = M.length;
        factions = new int[N];
        for(int i=0;i<N;++i){
            factions[i] = i;
        }
        for(int i=0;i<N-1;++i){
            for(int j=i+1;j<N;++j){
                if(M[i][j] != 0){
                    factions[root_get(j)] = root_get(i);
                }
            }
        }
        int ans = 0;
        int[] factions_alive = new int[N];
        for(int i=0;i<N;++i){
            if(factions_alive[root_get(i)]++ == 0){
                ++ans;
            }
        }
        return ans;
    }
}

