    def calcEquation(self, equations, values, queries):
        ancestor = {}#k:(v,k/v)
        def get_root(var):
            root,rat = ancestor[var]
            while root != ancestor[root][0]:
                root,ratp = ancestor[root]
                rat*=ratp
            ancestor[var] = (root,rat)
            return root,rat
        i = 0
        while i < len(equations):
            n,d = equations[i]
            v = values[i]
            if d in ancestor:
                anc,anc_rat = get_root(d)
                ancestor[n] = (anc,v*anc_rat)
            elif n in ancestor:
                anc,anc_rat = get_root(n)
                ancestor[d] = (anc,1/v*anc_rat)
            else:
                ancestor[d] = (d,1.0)
                i-=1
            i+=1
        results = []
        for n,d in queries:
            ans = -1.0
            if n in ancestor and d in ancestor:
                na,nar = get_root(n)
                da,dar = get_root(d)
                if na == da:
                    results.append(nar/dar)
                    continue
            results.append(ans)
        return results

