package main

import "fmt"

// Store number of connections of nodes
// Store lengths of all connections between pairs of nodes
// Note: There maybe be more than one connection between two nodes

// Data structure to represent the Raiload Map
type railMap struct {
	// Number of connections of each node
	connCounts []int
	// Slice of connection lengths between key1 and key2
	dists map[int]map[int][]int
}

func newRailMap(n int) *railMap {
	m := new(railMap)
	m.connCounts = make([]int, n)
	m.dists = make(map[int]map[int][]int, n)
	return m
}

// Get all neighbors of a node i
func (m *railMap) getNeighbors(i int) []int {
	neighbors := make([]int, 0, len(m.dists[i]))
	for n := range m.dists[i] {
		neighbors = append(neighbors, n)
	}
	return neighbors
}

// Add connection between a and b (may be equal) of lenght c
func (m *railMap) addConnection(a, b, c int) {
	if a == b {
		m.connCounts[a] += 2
		_, ok := m.dists[a]
		if !ok {
			m.dists[a] = make(map[int][]int, 10)
		}
		m.dists[a][a] = append(m.dists[a][a], c)
	} else {
		m.connCounts[a]++
		m.connCounts[b]++
		_, ok := m.dists[a]
		if !ok {
			m.dists[a] = make(map[int][]int, 10)
		}
		_, ok = m.dists[b]
		if !ok {
			m.dists[b] = make(map[int][]int, 10)
		}
		m.dists[a][b] = append(m.dists[a][b], c)
		m.dists[b][a] = append(m.dists[b][a], c)
	}
}

// Delete all connections between a and b (may not be equal)
func (m *railMap) removeConnections(a, b int) {
	m.connCounts[a]--
	m.connCounts[b]--
	delete(m.dists[a], b)
	delete(m.dists[b], a)
}

func test() {
	// Process input:
	var n, m, d int
	fmt.Scan(&n, &m)

	rails := newRailMap(n)

	for i := 0; i < m; i++ {
		var a, b, c int
		fmt.Scan(&a, &b, &c)
		a--
		b--

		rails.addConnection(a, b, c)
	}

	// Remove all nodes with exactly two connections:
	for i := 0; i < n; i++ {
		if rails.connCounts[i] != 2 {
			continue
		}

		neighbors := rails.getNeighbors(i)
		if len(neighbors) == 2 {
			// Node i has two distinct neighbors
			a, b := neighbors[0], neighbors[1]
			l1, l2 := rails.dists[i][a][0], rails.dists[i][b][0]

			rails.removeConnections(a, i)
			rails.removeConnections(b, i)
			rails.addConnection(a, b, l1+l2)
		} else {
			// Node i has two connections to only one neighbor (cycle)
			j := neighbors[0]
			ls := rails.dists[i][j]
			rails.removeConnections(i, j)
			rails.addConnection(j, j, ls[0]+ls[1])
		}
		d++
	}

	// Print new rail map:
	fmt.Println(m - d)
	for a := 0; a < n; a++ {
		if rails.connCounts[a] == 0 {
			continue
		}
		for b, c := range rails.dists[a] {
			if a > b {
				continue
			}
			for _, l := range c {
				fmt.Printf("%d %d %d\n", a+1, b+1, l)
			}
		}
	}
}

func main() {
	var t int
	fmt.Scan(&t)
	for i := 0; i < t; i++ {
		test()
		if i+1 < t {
			fmt.Println()
		}
	}
}

