import sys

bst = []
def add(cmd):
    new_node = [cmd[-1],None,None]
    if len(cmd) != 1:
        cur = 0
        for i in range(len(cmd)-2):
            cur = bst[cur][1 if cmd[i] == "L" else 2]
        bst[cur][1 if cmd[-2] == "L" else 2] = len(bst)
    bst.append(new_node)
def post_order():
    q = [bst[0]]
    vals = []
    while len(q) > 0:
        cur = q.pop()
        if not cur[1] and not cur[2]:
            vals.append(cur[0])
        else:
            q.append([cur[0],None,None])
            if cur[2]:
                q.append(bst[cur[2]])
            if cur[1]:
                q.append(bst[cur[1]])
    return ' '.join(vals)

for line in sys.stdin:
    add(line.strip())
print(post_order())

