public class Solution {

    // 1d-dp
    static long getWays(int n, int[] c) {
        long[] ways = new long[n+1];
        ways[0]=1;
        for(int coin:c){
            for(int i=0;i<n+1-coin;++i){
                if(ways[i]>0){
                    ways[i+coin]+=ways[i];
                }
            }
        }
        return ways[n];
    }

