int main() {
    int n; scanf("%d", &n);
    for (int t = 1; t <= n; ++t) {
        char number[256];
        char source[256];
        char dest[256];
        scanf("%s %s %s", number, source, dest);

        int source_len = strlen(source);
        int dest_len = strlen(dest);
        int number_len = strlen(number);

        // make mapping source
        map<char, int> source_to_int;
        for (int i = source_len - 1; i >= 0; --i) {
            source_to_int[source[i]] = i;
        }

        // make mapping dest 
        map<int, char> int_to_dest;
        for (int i = dest_len - 1; i >= 0; --i) {
            int_to_dest[i] = dest[i];
        }

        vector<int> converted;

        // get sum
        int sum = 0;
        int power = 0;
        for (int i = number_len - 1; i >= 0; --i) {
            sum += source_to_int[number[i]] * pow(source_len, power);
            ++power; 
        }

        // translate
        while (sum != 0) {
            converted.push_back(sum % dest_len);
            sum = sum - (sum % dest_len);
            sum /= dest_len;
        }

        // output
        cout << "Case #" << t << ": ";
        for (int i = converted.size() - 1; i >= 0; --i) {
            cout << int_to_dest[converted[i]];
        }
        cout << endl;

    }
    return 0;
}

